s start = 4070908800, secondsPerDay = 86400, sc = 1
//$zd((start+date)/86400) = date of sunday - first day of week
//End of week is saturday, date is seconds from <var>start<var>
//86400 - senonds in 24 hours
s num = 1
while $d(data(num,("date"))){
	if ((start+data(num,"date"))\secondsPerDay)>=(+$h){
		s num = num + 1
		Continue
		}
	s dateInternal = $zdt((start+data(num,"date"))\secondsPerDay,3)
	s amount = data(num,"amount")
	s unique = data(num,"unique")
	&sql(Insert Into History.Views (Repository, Amount, UniqueVisitors, DateOfData)
		Values (:data("RepositoryId"), :amount, :unique, :dateInternal))
	s sc = sc!SQLCODE
	Q:SQLCODE
	s num = num + 1
}
k data
Q sc
